def PatterCount(text, ptrn):
    count = 0
    for i in range(len(text)):
        s = text[i:len(ptrn)+i]
        if(len(s)<len(ptrn)):
            break
        if ptrn in s:
            count= count + 1
    print("El patrón se repite un número de veces de:"+str(count))
    return count

PatterCount("","CTGCCGACT")