#Esta Funcion recibe como parametro el texto que se quiere comparar, y el patron de comparacion

def PatternCount(Text, Pattern):
    count = 0
    for i in range(0, len(Text) - len(Pattern) + 1):
        if Text[i:i+len(Pattern)] == Pattern:
       		count += 1
    print('Cantidad:', count)
    return count    
PatternCount("GCGCG","GCG")

#Puntos Adicionales
PatternCount("","CTGCCGACT")

